                if (remember && user && pass) {
                    localStorage.setItem('username', user);
                    localStorage.setItem('password', pass);
                } else {
                    localStorage.setItem('username', '');
                    localStorage.setItem('password', '');
                }
                self.username('');
                self.password('');
